import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { RootState } from "../resume-store";

export type NameType = {
  first_name: string;
  middle_name: string;
  last_name: string;
};

type ContactType = {
  mail: string;
  phone: string;
  location: string;
  linkedin: string;
};

export type PersonBasics = {
  name: NameType;
  role: string;
  about_me: string;
  contact: ContactType;
};

export const fetchPersonData = createAsyncThunk(
  "basicInfos/fetchData",
  async () => {
    const response = await (
      await fetch(`http://localhost:3000/resume.json`)
    ).json();
    const data = await response.afk.basic_infos;
    console.log(`data: ${data}`);

    const person: PersonBasics = {
      name: {
        first_name: data.first_name,
        middle_name: data.middle_name,
        last_name: data.last_name,
      },
      role: data.role,
      about_me: data.about_me,
      contact: {
        mail: data.mail,
        phone: data.phone,
        location: data.location,
        linkedin: data.linkedin,
      },
    };
    return person;
  }
);

const initialState: PersonBasics = {
  name: { first_name: "first", middle_name: "middle", last_name: "last" },
  role: "role",
  about_me: "aboutme",
  contact: { mail: "mail", phone: "phone", location: "city", linkedin: "link" },
};

const basicInfoSlice = createSlice({
  name: "basicInfos",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(fetchPersonData.pending, (state) => {
      console.log("pending");
    });

    builder.addCase(fetchPersonData.rejected, (state) => {
      console.log("rejected");
    });

    builder.addCase(fetchPersonData.fulfilled, (state, action) => {
      state.name = action.payload.name;
      state.role = action.payload.role;
      state.contact = action.payload.contact;
      state.about_me = action.payload.about_me;
    });
  },
});

export const selectPersonBasics = (state: RootState) => {
  return state.basics;
};

export default basicInfoSlice.reducer;
