import { createSlice } from "@reduxjs/toolkit";
import { RootState } from "../resume-store";
import { fetchPersonData } from "./basic-infos-action";

export type NameType = {
  first_name: string;
  middle_name: string;
  last_name: string;
};

type ContactType = {
  mail: string;
  phone: string;
  location: string;
  linkedin: string;
};

export type PersonBasics = {
  name: NameType;
  role: string;
  about_me: string;
  contact: ContactType;
};

const initialState = {
  name: { first_name: "first", middle_name: "middle", last_name: "last" },
  role: "role",
  about_me: "aboutme",
  contact: { mail: "mail", phone: "phone", location: "city", linkedin: "link" },
} as PersonBasics;

const basicInfoSlice = createSlice({
  name: "basicInfos",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(fetchPersonData.fulfilled, (state, action) => {
      state.name = action.payload.name;
      state.role = action.payload.role;
      state.contact = action.payload.contact;
      state.about_me = action.payload.about_me;
    });
  },
});

export const selectPersonBasics = (state: RootState) => {
  return state.basics;
};

export default basicInfoSlice.reducer;
